var cp         = require('child_process');
var execFile   = cp.execFile;
var os         = require('os');

/*
 * Example:
 *
 * onlyif.rootInSmartosGlobal(function(err) {
 *     if (err) {
 *         console.log('Fatal: cannot run because: ' + err);
 *         process.exit(1);
 *     }
 *     console.log('hello root in the GZ!');
 * });
 * 
 */

exports.rootInSmartosGlobal = function (callback)
{
    if (process.getuid() !== 0) {
	return callback('you are not root.');
    }

    if (os.type() !== 'SunOS') {
        return callback('this is not SmartOS.');
    }

    execFile('/usr/bin/zonename', [], function (err, stdout, stderr) {
        var zonename;
        if (err) {
	    return callback('unable to run /usr/bin/zonename.');
        }
        zonename = stdout.replace(/\s+$/g, '');
        if (zonename !== 'global') {
            return callback('this is not the global zone.');
        }
        callback();
    });
}

